func main() {
	floor := 0
	notBeenToBasement := true
	for i:=0; i < len(input); i++ {
		switch input[i] {
		case '(':
			floor++
		case ')':
			floor--
		default:
			panic("unknown symbol: "+string(input[i]))
		}

		if floor < 0 && notBeenToBasement {
			fmt.Println("Basement trip at", i+1)
			notBeenToBasement = false
		}
	}

	fmt.Println("Final floor: ",floor)
}
